$(function() {
    if (typeof(Storage) !== "undefined") {
        if (sessionStorage.styleSheet) {
            if (Number(sessionStorage.styleSheet) != 0) {
                if (Number(sessionStorage.styleSheet) == 1) {
					$('#mainSheet').attr('href','http://revelo.amazyne.com/v-1.1/css/style.css');
				}
				if (Number(sessionStorage.styleSheet) == 2) {
					$('#mainSheet').attr('href','http://revelo.amazyne.com/v-1.1/blue/style.css');
				}
				if (Number(sessionStorage.styleSheet) == 3) {
					$('#mainSheet').attr('href','http://revelo.amazyne.com/v-1.1/red/style.css');
				}
				if (Number(sessionStorage.styleSheet) == 4) {
					$('#mainSheet').attr('href','http://revelo.amazyne.com/v-1.1/fuschia/style.css');
				}
				if (Number(sessionStorage.styleSheet) == 5) {
					$('#mainSheet').attr('href','http://revelo.amazyne.com/v-1.1/brown/style.css');
				}
				if (Number(sessionStorage.styleSheet) == 6) {
					$('#mainSheet').attr('href','http://revelo.amazyne.com/v-1.1/orange/style.css');
				}
            }
            else {
                if (Number(sessionStorage.styleSheet) == 0) {
                    
                }
            }
        }
    }
	if($(".navigation").hasClass('dark')){
		$('.header-dark').addClass('active');
		$('.header-light').removeClass('active');
	}
	else  {
		$('.header-light').addClass('active');
		$('.header-dark').removeClass('active');
	}
    $(".page-boxed")
        .click(function(event) {
            sessionStorage.bodyLayout = 1;
            location.reload()
        });
    $(".page-wide")
        .click(function(event) {
            sessionStorage.bodyLayout = 0;
            location.reload()
        });
    $(".header-dark")
        .click(function(event) {
            $(".top-header")
                .addClass("dark");
            $(".top-header .social-icons")
                .removeClass("si-6")
                .addClass("si-6-white");
            $(".navigation")
                .addClass("dark");
            $(".logo img")
                .attr("src", "images/logo-light.png");
            sessionStorage.headerColor = 1
        });
    $(".header-light")
        .click(function(event) {
            $(".top-header")
                .removeClass("dark");
            $(".top-header .social-icons")
                .removeClass("si-6-white")
                .addClass("si-6");
            $(".navigation")
                .removeClass("dark");
            $(".logo img")
                .attr("src", "images/logo.png");
            sessionStorage.headerColor = 0
        });
    $(".demo-panel a.switch-color")
        .click(function(e) {
            e.preventDefault();
			var base = "http://revelo.amazyne.com/v-1.1";
            var pathname = location.pathname.split("/")
                .slice(-1);
            if ($(this)
                .hasClass("default")) {
                var css = base + '/css/style.css';
				sessionStorage.styleSheet = 1;
            }
            else {
                if ($(this)
                    .hasClass("blue")) {
                    var folder = "blue";
					var css = base + '/' + folder + '/style.css';
                    sessionStorage.styleSheet = 2;
                }
                else {
                    if ($(this)
                        .hasClass("red")) {
                        var folder = "red";
						var css = base + '/' + folder + '/style.css';
                        sessionStorage.styleSheet = 3;
                    }
                    else {
                        if ($(this)
                            .hasClass("fuschia")) {
                            var folder = "fuschia";
							var css = base + '/' + folder + '/style.css';
                            sessionStorage.styleSheet = 4;
                        }
                        else {
                            if ($(this)
                                .hasClass("brown")) {
                                var folder = "brown";
								var css = base + '/' + folder + '/style.css';
                                sessionStorage.styleSheet = 5;
                            }
                            else {
                                if ($(this)
                                    .hasClass("orange")) {
                                    var folder = "orange";
                                    var css = base + '/' + folder + '/style.css';
									sessionStorage.styleSheet = 6;
                                }
                                
                            }
                        }
                    }
                }
            }
			
			$('#mainSheet').attr('href',css);
        });
    $("a.slider-arrow")
        .click(function(event) {
            event.preventDefault();
            $(".demo-panel")
                .toggleClass("active")
        })
});